average_system_temperature.value_template:
{% set domains = ['sensor', 'climate'] %} {% set temps = states
  | selectattr('domain', 'in', domains)
  | selectattr('entity_id', 'contains', 'temp')
  | rejectattr('entity_id', 'contains', 'battery')
  | rejectattr('entity_id', 'contains', 'average')
  | reject('none')
  | rejectattr('state', 'eq', 'unavailable')
  | map(attribute='state')
  | map('float')
  | list %}
{% if temps %}
  {{ temps | sum / temps | length | round(1) }}
{% else %}
  Unavailable
{% endif %}
